import './App.css';
import AddTask from './components/AddTask';
import DisplayTasks from "./components/DisplayTasks"

function App() {

  const [tasks,SetTasks] = useState([])

  return (
    <div className="App">

      <AddTask SetTasks={SetTasks}/>
      <DisplayTasks tasks={tasks} />
    </div>
  );
}

export default App;
