import React, { useEffect, useState } from 'react'
// import { useDispatch } from "react-redux"


/////MATERIAL UI //////////////////////////////////////////////////////////////////////////////////////
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import Button from '@material-ui/core/Button';

const useStyles = makeStyles((theme) => ({
    root: {
        '& > *': {
            margin: theme.spacing(1),
            width: '25ch',
        },
        formControl: {
            margin: theme.spacing(1),
            minWidth: 120,
        },
        selectEmpty: {
            marginTop: theme.spacing(2),
        },
    },
}));
////////////////////////////////////////////////////////////////////////////////////////////////////


const AddTask = (props) => {
    const classes = useStyles();
    const [input, SetInput] = useState("")
    const [status, SetStatus] = useState(false)

    const [tasks,SetTasks] = useState([])
    // const dispatch = useDispatch();

    useEffect((()=>{
        console.log("Tasks updated:", tasks);
        props.updatedTasks(tasks);

    }),[tasks])

    const handleChange = (e) => {
        // console.log("Event:", e.target.value)
        let value = e.target.value
        SetStatus(value)
    }


    const addTaskFunc = () => {
        console.log("Text:", input)
        console.log("Status:", status)
        // dispatch({
        //   type: "ADD",
        //   payload: {
        //     title: input,
        //     status: status,
        //   }
        // })
        SetTasks(prev => prev.length !== 0 ? [
            ...prev,
            {
                input: input,
                status: status
            }
        ] : [{
                input: input,
                status: status
            }])
        console.log("Tasks array:", tasks)
        // return (tasks)
    }

    return (
        <div>
            <h1>AddTask</h1>

            <TextField id="outlined-basic" label="Enter Task" variant="outlined" 
                onChange={e => SetInput(e.target.value)} />
            <br></br>

            {/* <input type="text" placeholder="Enter Title" onChange={e => SetInput(e.target.value)} /> <br></br> */}

            {/* <select onChange={e => handleChange(e)}>
        <option value="" > Select from below   </option>
        <option value={true} > True   </option>
        <option value={false} > False </option>
      </select> */}
            <br></br>

            <FormControl variant="outlined" className={classes.formControl}>
                <InputLabel htmlFor="outlined-age-native-simple">Status</InputLabel>
                <Select
                    native
                    value={status}
                    onChange={e => handleChange(e)}
                    label="Status"
                    inputProps={{
                        name: 'status',
                        id: 'outlined-age-native-simple',
                    }}
                >
                    <option aria-label="None" value="" />
                    <option value={true}>True</option>
                    <option value={false}>False</option>
                </Select>
            </FormControl>


            <br></br>
            <br></br>

            <Button variant="contained" color="primary" onClick={() => addTaskFunc()}>
                Add Task
      </Button>
            <br></br>
            <br></br>

            {/* <button onClick={addTaskFunc}>Add Task</button> */}
        </div>
    )
}

export default AddTask
