import React, { useState, useEffect } from 'react'
// import { useDispatch} from "react-redux"

///////////////////////////////////////////////////////////////////////////////////////////////
// MATERIAL UI
import { withStyles, makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  body: {
    fontSize: 14,
  },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    '&:nth-of-type(odd)': {
      backgroundColor: theme.palette.action.hover,
    },
  },
}))(TableRow);

const useStyles = makeStyles({
    table: {
      minWidth: 400,
        maxWidth: 600

    },
  });

  ///////////////////////////////////////////////////////////////////////////////////////////////
const DisplayTasks = (props) => {
    const classes = useStyles();

    console.log("Tasks in Display Tasks component:", props.tasks);

    const[updatedtasks,SetUpdatedTasks]= useState([])

    // var dispatch = useDispatch();

    useEffect((
      ()=>{
        SetUpdatedTasks(props.tasks)
      }
    ),[props])

    const toggleStatus = (i) => {
        console.log("Index", i , "is clicked")
            // dispatch({
            //     type: "TOGGLE",
            //     payload: {
            //         index: i,
            //     },
            // })
            SetUpdatedTasks(updatedtasks.map((item,ind) => {
              if(i ===ind) {
                  return{
                      ...item,
                      status: (!item.status),
                  }
              }
              else return item;
    }
            ))

  }
    // return (
    //     <div>
    //         <h1>Tasks</h1>
    //         <table border="1px" >
    //             <thead >
    //                 <tr>
    //                     <th>Task</th>
    //                     <th>Status (Completed or Not)</th>
    //                     <th>Toggle Status</th>
    //                 </tr>
    //             </thead>

    //             {tasks.map((task,index) => {

    //                 return (
    //                     <tbody>
    //                         <tr key={task.title}>
    //                             <td>{task.title}</td>
    //                             <td> {""+task.status === "true" ? "Done" : "Not Completed"}</td>
    //                             <td> <button onClick={()=>toggleStatus(index)}> Click to toggle</button></td>
    //                         </tr>
    //                     </tbody>
    //                 )

    //             })}
    //         </table>

    //     </div>
    // )
    return (
        <TableContainer component={Paper} >
          <Table className={classes.table} aria-label="customized table" style={{ width: 1000, margin: 'auto' }}>
            <TableHead>
              <TableRow>
                <StyledTableCell align="justify">Task</StyledTableCell>
                <StyledTableCell align="justify">Status</StyledTableCell>
                <StyledTableCell align="justify">Toggle Status</StyledTableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {updatedtasks.map((task,index) => (
                <StyledTableRow key={index}>
                  <StyledTableCell align="justify"component="th" scope="row"> ""+{task.input} </StyledTableCell>
                  <StyledTableCell align="justify">{""+task.status === "true" ? "Done" : "Not Completed"}</StyledTableCell>
                  <StyledTableCell align="justify"><button onClick={()=>toggleStatus(index)}> Click to toggle</button></StyledTableCell>
                </StyledTableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      );
}

export default DisplayTasks
